children.display:
(
  $arr := $all("postgres", "Lernziel2Inhalt", null, null, null, null, {"Lernziel": value.ID});
  $index := $floor($random()*$count($arr));
  $id := $arr[$index].Inhalt;
  $setVariable("Inhalt", $read("postgres", "Inhalt", $id).Inhalt);
  value.Ziel
)

children.print:
(
$setVariable("result" & value.ID, "");
$x := $curl("POST", "https://api.openai.com/v1/chat/completions", {
    "model": "gpt-4o-mini",
    "messages": [
      {
        "role": "system",
        "content": "Du bist ein Medizin Professor. Stelle mir eine Frage zu dem Text den ich Dir gebe."
      },
      {
        "role": "user",
        "content": variable.Inhalt
      }
    ]
 }, {"Authorization": "openai"}).choices[0].message.content;
$setVariable("frage" & value.ID, $x);
$refresh();
)

(
$x := $curl("POST", "https://api.openai.com/v1/chat/completions", {
    "model": "gpt-4o-mini",
    "response_format": { "type": "json_object" },
    "messages": [
      {
        "role": "system",
        "content": "Du bist ein strenger Medizin Professor. Stelle mir eine Fachfrage zu dem Text den ich Dir gebe. Anschließend bewertest Du die Antwort mit einem JSON Objekt, das die folgenden Felder enthält:
feedback: ein feedback mit 60 wörtern, mit Erklärung zur richtigen Antwort, Hinweise auf häufige Fehler und Tipps, wie das Lernziel besser verstanden werden kann
prozent: ein prozentwert der die güte der antwort beschreibt
"
      },
      {
        "role": "user",
        "content": variable.Inhalt
      },
      {
        "role": "assistant",
        "content": $lookup(variable, "frage" & value.ID)
      },
      {
        "role": "user",
        "content": form.antwort
      }
    ]
 }, {"Authorization": "openai"}).choices[0].message.content;
 $setVariable("result" & value.ID, $parseJson($x));
 $refresh();
)
