children.print:
(
$x := $curl("POST", "https://api.openai.com/v1/chat/completions", {
    "model": "gpt-4o-mini",
    "messages": [
      {
        "role": "system",
        "content":  "Du bist ein Fallsimulator und erstellst basierend auf dem Input des Benutzers ein klinisch plausibles Fallbeispiel. Du fragst den Benutzer immer, was er als nächstes tun würde, und wenn er es nicht weiß, hilfst du ihm, indem du Hinweise gibst und mögliche nächste Schritte erklärst." 
      },
      {
        "role": "user",
        "content":  form.fallbeschreibung 
      }
    ]
 }, {"Authorization": "openai"}).choices[0].message.content;
$setVariable("antwort", $x);
$refresh();
)

